odoo.script:
        $(document).ready(function () {
            var canvas = document.getElementById('signatureCanvas');

            if (canvas) {
                canvas.width = 350;
                canvas.height = 150;
            }

            var signaturePad = new SignaturePad(canvas);

            window.clearSignature = function () {
                signaturePad.clear();
                $('.signature-error').remove();
                $('#signatureCanvas').css('border', '3px solid #EE7929');
            };

            // Fonction de validation des champs
            function validateStep1() {
                var isValid = true;
                var requiredFields = ['nom', 'prenom', 'fonction', 'email', 'structure', 'contact'];

                // Réinitialiser les erreurs
                requiredFields.forEach(function(fieldId) {
                    $('#' + fieldId).removeClass('is-invalid');
                    $('#' + fieldId + '-error').hide();
                });

                // Vérifier chaque champ requis
                requiredFields.forEach(function(fieldId) {
                    var field = $('#' + fieldId);
                    var value = field.val().trim();

                    if (value === '') {
                        field.addClass('is-invalid');
                        $('#' + fieldId + '-error').show();
                        isValid = false;
                    }
                });

                return isValid;
            }

            // Supprimer l'erreur quand l'utilisateur tape dans un champ
            document.addEventListener('DOMContentLoaded', function() {
                var requiredFields = ['nom', 'prenom', 'fonction', 'email', 'structure', 'contact'];

                requiredFields.forEach(function(fieldId) {
                    var field = document.getElementById(fieldId);
                    if (field) {
                        field.addEventListener('input', function() {
                            var errorEl = document.getElementById(fieldId + '-error');
                            if (this.value.trim() !== '') {
                                this.classList.remove('is-invalid');
                                if (errorEl) errorEl.style.display = 'none';
                            }
                        });
                    }
                });
            });

            window.nextStep = function () {
                var currentStep = $('.step:visible');

                // Si on est à l'étape 1, valider les champs
                if (currentStep.attr('id') === 'step1') {
                    if (!validateStep1()) {
                        return false; // Empêcher le passage à l'étape suivante
                    }
                }

                var nextStep = currentStep.next('.step');
                if (nextStep.length) {
                    currentStep.hide();
                    nextStep.show();
                }
            };

            window.prevStep = function () {
                var currentStep = $('.step:visible');
                var prevStep = currentStep.prev('.step');
                if (prevStep.length) {
                    currentStep.hide();
                    prevStep.show();
                }
            };

            // Supprimer l'erreur quand l'utilisateur tape dans un champ
            $('input[required]').on('input', function() {
                var fieldId = $(this).attr('id');
                if ($(this).val().trim() !== '') {
                    $(this).removeClass('is-invalid');
                    $('#' + fieldId + '-error').hide();
                }
            });

            $('#presenceForm').on('submit', function (e) {
                e.preventDefault();

                if (signaturePad.isEmpty()) {
                    $('.signature-error').remove();
                    var errorMsg = '
' +
                                ' ' +
                                'La signature est obligatoire pour confirmer votre présence.' +
                                '';
                    // Ajouter le message en bas du conteneur du canvas
                    $('#signatureCanvas').parent().parent().append(errorMsg);
                    $('#signatureCanvas').css('border', '3px solid #dc3545');
                    return false;
                }

                $('.signature-error').remove();
                $('#signatureCanvas').css('border', '3px solid #EE7929');

                var signatureData = signaturePad.toDataURL('image/png');
                $('#signature').val(signatureData);

                e.target.submit();
            });

            $('.step').hide();
            $('#step1').show();
        });

div.script:
let currentStep = 1;
let signaturePad = null;

// Définir nextStep comme fonction globale pour le template meeting_already_signe_in
if (typeof window.nextStep === 'undefined') {
    window.nextStep = function() {
        // Si on est à l'étape 1, valider les champs
        if (currentStep === 1) {
            if (!validateStep1()) {
                return; // Empêcher le passage à l'étape suivante
            }
        }

        document.getElementById('step' + currentStep).style.display = 'none';
        currentStep++;
        const nextStepEl = document.getElementById('step' + currentStep);
        if(nextStepEl) nextStepEl.style.display = 'block';
    }
}

// Définir prevStep comme fonction globale pour le template meeting_already_signe_in
if (typeof window.prevStep === 'undefined') {
    window.prevStep = function() {
        document.getElementById('step' + currentStep).style.display = 'none';
        currentStep--;
        const prevStepEl = document.getElementById('step' + currentStep);
        if(prevStepEl) prevStepEl.style.display = 'block';
    }
}

// Fonction de validation des champs
function validateStep1() {
    var isValid = true;
    var requiredFields = ['nom', 'prenom', 'fonction', 'email', 'structure', 'contact'];

    // Réinitialiser les erreurs
    requiredFields.forEach(function(fieldId) {
        var field = document.getElementById(fieldId);
        var errorEl = document.getElementById(fieldId + '-error');
        if (field) field.classList.remove('is-invalid');
        if (errorEl) errorEl.style.display = 'none';
    });

    // Vérifier chaque champ requis
    requiredFields.forEach(function(fieldId) {
        var field = document.getElementById(fieldId);
        var errorEl = document.getElementById(fieldId + '-error');

        if (field && field.value.trim() === '') {
            field.classList.add('is-invalid');
            if (errorEl) errorEl.style.display = 'block';
            isValid = false;
        }
    });

    return isValid;
}

// Fonction pour valider la signature
function validateSignature() {
    if (signaturePad && signaturePad.isEmpty()) {
        // Supprimer l'ancien message d'erreur s'il existe
        document.querySelectorAll('.signature-error').forEach(el => el.remove());

        // Créer un message d'erreur
        var errorDiv = document.createElement('div');
        errorDiv.className = 'alert alert-danger signature-error mt-2 text-center';
        errorDiv.style.animation = 'fadeIn 0.3s ease-in';
        errorDiv.innerHTML = ' ' +
                         'La signature est obligatoire pour confirmer votre présence.';

        // Ajouter le message en bas du conteneur du canvas
        var canvas = document.getElementById('signatureCanvas');
        canvas.parentNode.parentNode.append(errorDiv);

        // Bordure rouge
        canvas.style.border = '3px solid #dc3545';

        return false;
    }

    // Nettoyer les erreurs et remettre la bordure normale
    document.querySelectorAll('.signature-error').forEach(el => el.remove());
    var canvas = document.getElementById('signatureCanvas');
    if (canvas) canvas.style.border = '3px solid #EE7929';

    return true;
}

document.addEventListener("DOMContentLoaded", function() {
    // Masquer tous les steps sauf le premier
    let steps = document.querySelectorAll('.step');
    steps.forEach((el, i) => { if(i > 0) el.style.display = 'none'; });

    // Initialiser le canvas de signature seulement s'il existe (pas de signature valide)
    var canvas = document.getElementById("signatureCanvas");
    var hasValidSignature = ;
    if (canvas && !hasValidSignature) {
        // Définir la taille du canvas
        canvas.width = 350;
        canvas.height = 150;

        signaturePad = new SignaturePad(canvas, {
            backgroundColor: 'rgba(255, 255, 255, 0)',
            penColor: 'rgb(0, 0, 0)'
        });

        var sigInput = document.getElementById("signature");

        // Charger la signature existante si elle existe
        if (sigInput.value && sigInput.value.trim() !== '') {
            try {
                console.log('Chargement de la signature existante...');
                signaturePad.fromDataURL(sigInput.value);
            } catch(e) {
                console.error('Erreur lors du chargement de la signature:', e);
                sigInput.value = '';
                signaturePad.clear();
            }
        }
    }

    // Fonction pour effacer la signature
    window.clearSignature = function() {
        if(signaturePad) {
            signaturePad.clear();
            document.getElementById("signature").value = '';
            // Supprimer les messages d'erreur
            document.querySelectorAll('.signature-error').forEach(el => el.remove());
            // Remettre la bordure normale
            var canvas = document.getElementById('signatureCanvas');
            if (canvas) canvas.style.border = '3px solid #EE7929';
        }
    }

    // Vérifier si on doit valider la signature
    var needsSignatureValidation = !hasValidSignature;

    // Validation et soumission du formulaire
    document.getElementById("presenceForm").addEventListener("submit", function(e) {
        e.preventDefault(); // Empêcher la soumission par défaut

        // Valider la signature
        if (!validateSignature()) {
            return false;
        }

        // Si validation OK, sauvegarder et soumettre
        if(signaturePad && !signaturePad.isEmpty()) {
            document.getElementById("signature").value = signaturePad.toDataURL();
        }

        // Soumettre le formulaire
        this.submit();
    });
});
